mapper.select:
SELECT sku.id, sku.sku_code, sku.sku_name, sku.sku_type, sku.sell_price, sku.in_price, sku.stock_num, sku.create_time, sku.update_time, sku.opreator_id, sku.opreator_name, pr.id rule_id, pr.rule_type, pr.rule_desc, pr.end_time, pr.shop_id, pr.sku_id, cs.amount, (select count(1) from coupon where sku_id = sku.id) sell_num FROM sku sku LEFT JOIN promotion_rules pr ON sku.id = pr.sku_id LEFT JOIN coupon_sku cs on sku.id = cs.sku_id
sku.id = #{id,jdbcType=BIGINT}
and sku.sku_code = #{skuCode,jdbcType=VARCHAR}
and sku.sku_name like concat('%', #{skuName,jdbcType=VARCHAR}, '%')
order by sku.id desc limit #{start}, #{rows}

select count(1)
from sku


    id = #{id,jdbcType=BIGINT}


    and sku_code = #{skuCode,jdbcType=VARCHAR}


    and sku_name like concat('%', #{skuName,jdbcType=VARCHAR}, '%')
